import React,{useState} from 'react'

const GrandChild = props => {
    return(<Child brand={props}/>
  )
}

const Child = props => {
    return(
      <h1>Brand: props.brand</h1>
    )
}

const App = () =>{
  const [brandname] = useState('Amazon')
  return(
    <div>
      <h1>Hello</h1>
      <GrandChild brand={brandname} />
    </div>
  )
}

export default App
