import React, { Fragment } from 'react'

import Provider from "./Provider"
import Context from "./Context"

const PassMission1 = () => {
  return(
    <PassMission2 />
  )
}

const PassMission2 = () =>{
  return(
    <Context.Consumer>
      {
        context => (
          <Fragment>
            <h1>Name of the Mission: {context.data.name}</h1>
          </Fragment>
        )
      }
    </Context.Consumer>
  )
}

const App = () => {
  return(
    <Fragment>
      <h1>Calling Provider</h1>
      <Provider>
        <PassMission1 />
      </Provider>
    </Fragment>
  )
}

export default App
